mapper.select:
SELECT *
FROM lc_info
WHERE year=#{year} AND lc=#{lc}

SELECT lc
FROM lc_info
WHERE year=${year}

(
    select i2.* from
    (
        select fgMemberId1
        from lc_info
        where year=#{year} and lc=#{lc}
    ) as i1
    left join
    (
        select *
        from fg_member_info
    ) as i2
    on i1.fgMemberId1=i2.fgMemberId
)
union all
(
    select i2.* from
    (
        select fgMemberId2
        from lc_info
        where year=#{year} and lc=#{lc}
    ) as i1
    left join
    (
        select *
        from fg_member_info
    ) as i2
    on i1.fgMemberId2=i2.fgMemberId
);
